namespace PasswordManager;

class Program
{
    static void Main(string[] args)
    {
        Console.WriteLine(LogoGenerator());
        PasswordService passwordService = new PasswordService();
        Console.WriteLine("Witaj w Locker v: 1.0.0");
        ConsoleKeyInfo action;
        do
        {
            ViewMessages.StartUpMenu();
            action = Console.ReadKey();
            switch (action.KeyChar)
            {
                case '1':
                    passwordService.AddNewPassword();
                    break;
                case '2':
                    PasswordsListView(passwordService.GetAllPasswords());
                    break;
                case '3':
                    passwordService.GetPasswordByWebName();
                    break;
                case '4':
                    passwordService.RemovePassword();
                    break;
                case '5':
                    Environment.Exit(0);
                    break;
            }
        } while (action.KeyChar != '5');
    }

